sn_aia_tool.script:
(function(inputs) {

    var res = []; //empty array to store results
    var count = 0; //counter for bucket sequence

    //create new GlideRecord of sys_choice table and filter by name(defect table), element(bucket), and active records, and order by sequence
    var defect_dict = new GlideRecord("sys_choice");
    defect_dict.addQuery("name", "x_snc_defect_bucke_gaic_defects");
    defect_dict.addQuery("element", "bucket");
    defect_dict.addQuery("inactive", false);
    defect_dict.orderBy("sequence");
    defect_dict.query();

    //iterate through each record and push string to the result array
    while (defect_dict.next()) {
        count++;
        var label = defect_dict.getValue("label");
        var value = defect_dict.getValue("value");
        res.push(`Bucket ${count}: ${label}, (field_value=${value})`);
    }

    if (count == 0)
        return gs.getMessage("No available bucket");
    else
        return res.join(";\n"); //return result array
})(inputs);
