sn_aia_tool.script:
(function(inputs) {
    // Create a new GlideRecord object for the 'sys_choice' table
    var records = new GlideRecord('sys_choice');
    // Add a query to filter records by 'name' field
    records.addQuery('name', 'x_snc_defect_bucke_gaic_defects');
    // Add a query to filter records by 'element' field
    records.addQuery('element', 'bucket');
    // Add a query to filter records by 'value' field
    records.addQuery('value', inputs.bucketName);
    // Limit the query results to 1 record
	records.setLimit(1);
    // Execute the query
    records.query();
    // Check if no record exists with the given bucket name
    if (!records.next()) {
        // Create a new GlideRecord object for the 'sys_choice' table
        var newChoice = new GlideRecord('sys_choice');
        // Initialize the new record
        newChoice.initialize();
        // Set the 'name' field of the new record
        newChoice.setValue('name', 'x_snc_defect_bucke_gaic_defects');
        // Set the 'element' field of the new record
        newChoice.setValue('element', 'bucket');
        // Set the 'value' field of the new record
        newChoice.setValue('value', inputs.bucketName);
        // Set the 'label' field of the new record
        newChoice.setValue('label', inputs.bucketName);
        // Insert the new record into the database
        newChoice.insert();
    }
})(inputs);
